import React, { useEffect, useState } from "react";
import './styles/App.css';
import twitterLogo from './assets/twitter-logo.svg';
import { ethers } from "ethers"
import myNft from "./GenerativeNFT.json"

const TWITTER_HANDLE = 'nisam_ozr';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

const TOTAL_MINT_COUNT = 50;

const CONTRACT_ADDRESS = "0x91d34061cF430F45b0388642CEFEA943152cb7Db";

const App = () => {
  const [currentAccount, setCurrentAccount] = useState("");

  const checkIfWalletIsConnected = async () => {
    const { ethereum } = window;

    if (!ethereum) {
      console.log("Make sure you have metamask!");
      return;
    } else {
      console.log("We have the ethereum object", ethereum);
    }

    const accounts = await ethereum.request({ method: 'eth_accounts' });

    if (accounts.length !== 0) {
      const account = accounts[0];
      console.log("Found an authorized account:", account);
      setCurrentAccount(account)

      setupEventListener()
    } else {
      console.log("No authorized account found")
    }
  }


  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("Get MetaMask!");
        return;
      }

      const accounts = await ethereum.request({ method: "eth_requestAccounts" });

      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]);
      setupEventListener()
    } catch (error) {
      console.log(error)
    }
  }



  const setupEventListener = async () => {
    // Most of this looks the same as our function askContractToMintNft
    try {
      const { ethereum } = window;

      if (ethereum) {
        // Same stuff again
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const connectedContract = new ethers.Contract(CONTRACT_ADDRESS, myNft.abi, signer);

        // THIS IS THE MAGIC SAUCE.
        // This will essentially "capture" our event when our contract throws it.
        // If you're familiar with webhooks, it's very similar to that!
        connectedContract.on("NewEpicNFTMinted", (from, tokenId) => {
          console.log(from, tokenId.toNumber())
          alert(`Hey there! We've minted your NFT and sent it to your wallet. It may be blank right now. It can take a max of 10 min to show up on OpenSea. Here's the link: https://testnets.opensea.io/assets/${CONTRACT_ADDRESS}/${tokenId.toNumber()}`)
        });

        console.log("Setup event listener!")

      } else {
        console.log("Ethereum object doesn't exist!");
      }
    } catch (error) {
      console.log(error)
    }
  }

  const askContractToMintNft = async () => {


    try {
      const { ethereum } = window;

      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const connectedContract = new ethers.Contract(CONTRACT_ADDRESS, myNft.abi, signer);

        console.log("Going to pop wallet now to pay gas...")
        let nftTxn = await connectedContract.makeAnEpicNFT();

        console.log("Mining...please wait.")
        await nftTxn.wait();

        console.log(`Mined, see transaction: https://rinkeby.etherscan.io/tx/${nftTxn.hash}`);

      } else {
        console.log("Ethereum object doesn't exist!");
      }
    } catch (error) {
      console.log(error)
    }
  }

  useEffect(() => {
    checkIfWalletIsConnected();
  }, [])

  const renderMintUI = () => (
    <button onClick={askContractToMintNft} className="cta-button connect-wallet-button">
      Mint NFT
    </button>
  );

  const renderNotConnectedContainer = () => (
    <button onClick={connectWallet} className="cta-button connect-wallet-button">
      Connect to Wallet
    </button>
  );
  const OPENSEA_LINK = () => {
    window.location.href = "https://testnets.opensea.io/collection/the-quate-v3"
  };

  return (
    <div className="App">
      {/* <div className="container"> */}
        {/* <div className="header-container">
          <p className="header gradient-text">My NFT Collection</p>
          <p className="sub-text">
            Each unique. Each beautiful. Discover your NFT today.
          </p>
          {currentAccount === "" ? (
            renderNotConnectedContainer()
          ) : (
            renderMintUI()
          )}
        </div>
        <button onclick={OPENSEA_LINK} className="cta-button connect-wallet-button"
        > opensea</button>


        <div className="footer-container">
          <img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
          <a
            className="footer-text"
            href={TWITTER_LINK}
            target="_blank"
            rel="noreferrer"
          >{`Nisam_ozr @ ${TWITTER_HANDLE}`}</a>
        </div>
      </div> */}

      <div className="container">

        <div className="nav header-container">

          <div><p className="header gradient-text nav-title">My NFT Collection</p> </div>
          <div className="connect">
            <button onClick={connectWallet} className="cta-button connect-wallet-button">
              Connect to Wallet
            </button>
          </div>
          {/* <hr /> */}

        </div>

        <div className="container">
          <div className="row body">
            <div className="col-md-6">
              <div className="mb-5">
            <p className="header gradient-text">My NFT Collection</p> 
            <p className="sub-text">
            Each unique. Each beautiful. Discover your NFT today.

          </p>
          </div>
          <div className=" mint_div ">
          <button onClick={askContractToMintNft} className="cta-button connect-wallet-button">
      Mint NFT
    </button>
    <a href=""><img className="opensee" src="https://storage.googleapis.com/opensea-static/Logomark/Logomark-White.png" alt="" srcset="" /></a>

    <a href=""><img className="rairble opensee" src=" https://global-uploads.webflow.com/60f008ba9757da0940af288e/60f29bbc7abeec44f5e53996_rarible.jpg" alt="" srcset="" /></a>

          </div>

            </div>
            <div className="col-md-6 img">
              <img src="https://storage.opensea.io/files/b2e7e4483ab506742cefd228f5ed7a6b.svg" alt="" />

            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
